const double sr = sin(m_roll);
const double sp = sin(m_pitch);
const double sy = sin(m_yaw);
const double cr = cos(m_roll);
const double cp = cos(m_pitch);
const double cy = cos(m_yaw);

const double spsy = sp * sy;
const double spcy = sp * cy;

return DCM(cp*cy, cp*sy, -sp,
        sr*spcy - cr*sy, sr*spsy + cr*cy, sr*cp,
        cr*spcy + sr*sy, cr*spsy - sr*cy, cr*cp);

units::Radians angle = units::Radians(m_roll * 0.5);
const double sr = sin(angle);
const double cr = cos(angle);

angle = units::Radians(m_pitch * 0.5);
const double sp = sin(angle);
const double cp = cos(angle);

angle = units::Radians(m_yaw * 0.5);
const double sy = sin(angle);
const double cy = cos(angle);

const double cpcy = cp * cy;
const double spcy = sp * cy;
const double cpsy = cp * sy;
const double spsy = sp * sy;

return Quaternion(cr * cpcy + sr * spsy,
        Vec3(sr * cpcy - cr * spsy,
            cr * spcy + sr * cpsy,
            cr * cpsy - sr * spcy));

Produce the product of two quaternions.
@param rhs Quaternion to mutiply by.
@return Return the product of the two quaternions Q = q * rhs;

double w = m_w * rhs.m_w - m_v(1) * m_v(1) - m_v(2) * rhs.m_v(2) - (m_v(3) * rhs.m_v(3));
double x = m_w * rhs.m_v(1) + m_v(1) * m_w + m_v(2) * rhs.m_v(3) - m_v(3) * rhs.m_v(2);
double y = m_w * rhs.m_v(2) - m_v(1) * m_v(3) + m_v(2) * rhs.m_w + m_v(3) * rhs.m_v(1);
double z = m_w * rhs.m_v(3) + m_v(1) * m_v(2) - m_v(2) * rhs.m_v(1) + m_v(3) * rhs.m_w;
return Quaternion(w, Vec3(x, y, z));

Assign the product of two quaternions to this class.
@param rhs Quaternion to mutiply by.
@return Return this class after multiplication. Q *= rhs;

Perform a vector rotation.
@param v Vector to be rotated.
@return The rotated vector.

/* Because we always keep the quaternion normalized, the inverse is the
 * conjugate.
 */
return conjugate();

const double sqw = m_w * m_w;
const double sqx = m_v(1) * m_v(1);
const double sqy = m_v(2) * m_v(2);
const double sqz = m_v(3) * m_v(3);

const units::Radians roll =
    units::Radians(atan2(2 * (m_v(2) * m_v(3) + m_v(1) * m_w),
                sqw - sqx - sqy + sqz));

const units::Radians pitch =
    units::Radians(asin2(-2 * (m_v(1) * m_v(3) - m_v(2) * m_w)));

const units::Radians yaw =
    units::Radians(atan2(2 * (m_v(1) * m_v(2) + m_v(3) * m_w),
                sqw + sqx - sqy - sqz));

return Euler(roll, pitch, yaw);

double mag2 = m_w * m_w + m_v(1) * m_v(1) + m_v(2) * m_v(2) +
    m_v(3) * m_v(3);

/* Only normalize the quaternion if the magnitude diverges from 1 by
 * more than some epsilon to make more efficient.
 * We use the magnitude squared value because it should still be near 1
 * and the sqrt() function is expensive. If you've already done the
 * sqrt() then you might as well divide.
 */
if(fabs(mag2 - 1) > QUAT_NORMALIZATION_EPSILON)
{
    double mag = sqrt(mag2);

    m_w /= mag;
    m_v /= mag;

    if(fabs(m_w) <= NEAR_ZERO)
    {
        m_w = 0;
    }
}

