CPPUNIT_ASSERT(m != n);

Matrix3 m(1, 2, 3, 4, 5, 6, 7, 8, 9);

/* m is initially a singular matrix, therefore, it's determinate should
 * be 0.
 */
CPPUNIT_ASSERT(0 == m.determinant());
CPPUNIT_ASSERT_THROW(m.inverse(), int);

m(3,3) = 0;
Matrix3 n = m.inverse();
Matrix3 r = (n * 10000) / 10000;

DPRINTF("\n\n");
for(int i = 1; i <=3; ++i)
{
    for(int j = 1; j <=3; ++j)
    {
        r(i,j) = round(r(i,j) * 10000);
        DPRINTF("r(%d,%d) = %g%c", i, j, r(i,j), (3 == j) ? '\n' : ' ');
    }
}
DPRINTF("\n");

CPPUNIT_ASSERT(
        (-17778 == r(1,1)) &&
        (8889 == r(1,2)) &&
        (-1111 == r(1,3)) &&
        (15556 == r(2,1)) &&
        (-7778 == r(2,2)) &&
        (2222 == r(2,3)) &&
        (-1111 == r(3,1)) &&
        (2222 == r(3,2)) &&
        (-1111 == r(3,3)));

r = n.inverse();
DPRINTF("\n\n");
for(int i = 1; i <=3; ++i)
{
    for(int j = 1; j <=3; ++j)
    {
        r(i,j) = round(r(i,j));
        DPRINTF("r(%d,%d) = %g%c", i, j, r(i,j), (3 == j) ? '\n' : ' ');
    }
}
DPRINTF("\n");

CPPUNIT_ASSERT(m == r);

